INSERT INTO `game` VALUES ('6', 'Xâm nhập 2', 'Xam-nhap-2.jpg', 'Xam-nhap-2.swf', '<div class=\"textview instruction\">\r\n                        <img class=\"screenshot\" src=\"https://st.gamevui.com/images/image/gamehanhdong/xam-nhap-2.jpg\" title=\"Xâm nhập 2\" alt=\"Xâm nhập 2\">\r\n                    <p style=\"text-align: justify; \">Tiếp tục với một nhiệm vụ xâm nhập khác, bạn sẽ phải tiêu diệt toàn bộ bọn khủng bố chiếm đóng tại đó. Những khó khăn, nguy hiểm nào đang chờ bạn?</p>\r\n<p style=\"text-align: justify; \">Di chuyển&nbsp;<img src=\"https://st.gamevui.com/images/Image/Icon_HD/a.png\" alt=\"\"><img src=\"https://st.gamevui.com/images/Image/Icon_HD/d.png\" alt=\"\"><img src=\"https://st.gamevui.com/images/Image/Icon_HD/s.png\" alt=\"\"><img src=\"https://st.gamevui.com/images/Image/Icon_HD/w.png\" alt=\"\">, thay súng&nbsp;<img src=\"https://st.gamevui.com/images/Image/Icon_HD/1.png\" alt=\"\"><img src=\"https://st.gamevui.com/images/Image/Icon_HD/2.png\" alt=\"\"><img src=\"https://st.gamevui.com/images/Image/Icon_HD/3.png\" alt=\"\"><img src=\"https://st.gamevui.com/images/Image/Icon_HD/4.png\" alt=\"\">, bắn&nbsp;<img src=\"https://st.gamevui.com/images/Image/Icon_HD/mouse_left.png\" alt=\"\">.</p>\r\n\r\n\r\n                </div>', '59943', '4922', '0', '2018-09-01 19:52:45', '1', '1');
